select count(*) from SYS_DICT sd
where 1=1


    AND sd.DICT_NAME = #{dictName}



    AND sd.DICT_CODE = #{dictCode}



    AND sd.SORT = #{sort}



    AND sd.STATUS = #{status}



    AND sd.UPDATE_ID = #{updateId}

order by sd.ID asc

