select count(*) from SYS_ROLE sr
where 1=1


    AND sr.ROLE_NAME = #{roleName}



    AND sr.CREATE_TIME = #{createTime}



    AND sr.CREATE_ID = #{createId}



    AND sr.STATUS = #{status}

order by sr.ID asc

